import React, { useCallback, useState, useEffect } from 'react';
import {
  StyleSheet,
  Text,
  View,
  Image,
  Alert,
  Modal,
  TouchableHighlight,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import * as Yup from 'yup';
import {} from 'formik';

import Screen from './Screen';
import Form from '../component/Form/Form';
import FormField from '../component/Form/FormField';
import SubmitButton from '../component/Form/SubmitButton';
import Button from '../component/Button';
import colors from '../config/colors';
import Picker from '../component/Form/FormPicker';
import CategoryPickerItem from '../component/CategoryPickerItem';
import MyImagePicker from '../component/ImagePicker';
import { get, post } from '../common/http';

const width = 300;

const initialValues = {
  title: '',
  price: '',
  desc: '',
  category: null,
};

const validationSchema = Yup.object().shape({
  title: Yup.string().required().min(1).label('Title'),
  price: Yup.number().required().min(0.01).max(10000).label('Price'),
  desc: Yup.string().label('Description'),
  category: Yup.object().required().nullable().label('Category'),
});

//create your forceUpdate hook
function useForceUpdate() {
  const [value, setValue] = useState(0); // integer state
  return () => setValue((value) => ++value); // update the state to force render
}

export default function Add({ navigation }) {
  const [images, setImages] = useState([1]);
  const [categories, setCategories] = useState([]);
  const [imageUrl, setImageUrl] = useState<Array<string>>([]);
  // const [modalVisible, setModalVisible] = useState(false);
  const forceUpdate = useForceUpdate();
  const [key, setKey] = useState(0);

  useEffect(() => {
    const getAllCategory = async () => {
      const response = await get('/category/getAll');
      if (response.code === 0) {
        const { categories } = response.data;
        setCategories(categories);
      }
    };
    getAllCategory();
  }, []);

  useEffect(() => {
    const unsubscribe = navigation.addListener('focus', () => {
      setKey(key + 1);
    });
    // Return the function to unsubscribe from the event so it gets removed on unmount
    return unsubscribe;
  }, []);

  const handlePick = useCallback(
    async (image) => {
      //   console.log(image);
      const fileName = image.uri.split('/')[-1];
      try {
        const response = await post(
          `http://hn216.api.yesapi.cn/?s=App.CDN.UploadImgByBase64&app_key=5687BCD24AA4D3C1ED073F5C8AC17C6B&sign=wtOVTtR1veX4VVwgSkYMf0Ur9YVHsifAhGl55hbXMrQbwGKWkPmBAEHoo5ydejQncZWI5b&file_name=${fileName}`,
          {
            file:
              'data:image/png;base64,',
          },
          true
        ).catch((e) => {
          throw e;
        });
        if (response.data?.err_code === 0) {
          const temp = [...imageUrl];
          temp.push(response.data?.url);
          setImageUrl(temp);
          if (images.length === 3) return;
          const newImages = [...images];
          newImages.push(1);
          setImages(newImages);
          return true;
        } else {
          return false;
        }
      } catch (e) {
        return false;
      }
    },
    [images, imageUrl]
  );

  const handleAdd = useCallback(
    async (values, actions) => {
      let user = await AsyncStorage.getItem('user');
      user = user ? JSON.parse(user) : null;
      const { category, desc, title, price } = values;
      const response = await post('/list/add', {
        category: category._id,
        title,
        desc,
        price,
        images: [...imageUrl],
        user: user?._id,
      });
      if (response.code === 0) {
        Alert.alert('success', 'Add Success');
        // setModalVisible(true);
        // actions.resetForm(initialValues);
        navigation.navigate('MyListings');
      } else {
        Alert.alert('Error', 'Add Failed');
      }
    },
    [imageUrl]
  );

  return (
    <View style={styles.container}>
      <View style={styles.imagePickerWrapper}>
        {images.map((item, index) => {
          return (
            <MyImagePicker
              key={index}
              callBack={handlePick}
              isBase64={true}
              otherStyle={{
                borderRadius: 0,
                width: 100,
                marginLeft: 15,
                height: 100,
                defaultImage: { height: 50, width: 50 },
                image: { width: 100, height: 100, borderRadius: 0 },
                backgroundColor: '#f7f4f4',
              }}
            ></MyImagePicker>
          );
        })}
      </View>
      {/* @ts-ignore */}
      <Form
        initialValues={initialValues}
        onSubmit={handleAdd}
        validationSchema={validationSchema}
        key={key}
      >
        <FormField
          maxLength={50}
          name="title"
          placeholder="Title"
          width={width}
        />
        <FormField
          keyboardType="numeric"
          maxLength={6}
          name="price"
          placeholder="Price"
          width={120}
        />
        <Picker
          items={categories}
          name="category"
          numberOfColumns={3}
          PickerItemComponent={CategoryPickerItem}
          placeholder="Category"
          width="50%"
        />
        <FormField
          maxLength={255}
          multiline
          name="desc"
          numberOfLines={3}
          placeholder="Description"
        />
        <SubmitButton title="Post" />
      </Form>
      {/* <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <MyText style={styles.modalText}>发布成功！</MyText>
            <TouchableHighlight
              style={{ ...styles.openButton, backgroundColor: '#2196F3' }}
              onPress={() => {
                setModalVisible(!modalVisible);
              }}
            >
              <Text style={styles.textStyle}>确定</Text>
            </TouchableHighlight>
          </View>
        </View>
      </Modal> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 10,
    paddingHorizontal: 15,
  },
  imagePickerWrapper: {
    marginBottom: 20,
    flexDirection: 'row',
  },
  centeredView: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 22,
  },
  modalView: {
    margin: 20,
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 35,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  modalText: {
    marginBottom: 15,
    textAlign: 'center',
  },
  openButton: {
    backgroundColor: '#F194FF',
    borderRadius: 20,
    padding: 10,
    paddingHorizontal: 20,
    elevation: 2,
  },
  textStyle: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
});
